// src/PaymentPage.js
import React, { useState } from "react";
import "./PaymentPage.css"; // Make sure to create this CSS file
import { useNavigate } from "react-router-dom";

const PaymentPage = () => {
  const [selectedPayment, setSelectedPayment] = useState("");
  const navigate = useNavigate();

  const handleProceed = () => {
    alert(`You selected ${selectedPayment} as payment method.`);
    // Here you can navigate to order confirmation or success page
    navigate("/order-success"); 
  };

  return (
    <div className="payment-page">
      <h2>Choose Payment Method</h2>

      <div className="payment-options">
        {/* PhonePe */}
        <label className="payment-option">
          <input
            type="radio"
            name="payment"
            value="PhonePe"
            checked={selectedPayment === "PhonePe"}
            onChange={(e) => setSelectedPayment(e.target.value)}
          />
          PhonePe
        </label>

        {/* EMI */}
        <label className="payment-option">
          <input
            type="radio"
            name="payment"
            value="EMI"
            checked={selectedPayment === "EMI"}
            onChange={(e) => setSelectedPayment(e.target.value)}
          />
          EMI
        </label>

        {/* Net Banking */}
        <label className="payment-option">
          <input
            type="radio"
            name="payment"
            value="Net Banking"
            checked={selectedPayment === "Net Banking"}
            onChange={(e) => setSelectedPayment(e.target.value)}
          />
          Net Banking
        </label>

        {/* Debit/Credit Card */}
        <label className="payment-option card-option">
          <input
            type="radio"
            name="payment"
            value="Card"
            checked={selectedPayment === "Card"}
            onChange={(e) => setSelectedPayment(e.target.value)}
          />
          Debit/Credit Card
        </label>

        {/* Card details form if Card is selected */}
        {selectedPayment === "Card" && (
          <div className="card-details">
            <input type="text" placeholder="Name on Card" required />
            <input type="text" placeholder="Card Number" required />
            <input type="text" placeholder="Expiry Date (MM/YY)" required />
            <input type="text" placeholder="CVV" required />
          </div>
        )}
      </div>

      <button
        className="proceed-payment-btn"
        disabled={!selectedPayment}
        onClick={handleProceed}
      >
        Proceed
      </button>
    </div>
  );
};

export default PaymentPage;
