// src/Checkout.js
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Checkout.css";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const rawItems =
    location.state?.selectedItems || location.state?.product || [];
  const selectedItems = Array.isArray(rawItems) ? rawItems : [rawItems];

  // Add delivery charge ONCE per product
  const itemsWithDelivery = selectedItems.map((item) => ({
    ...item,
    deliveryCharge:
      item.deliveryCharge ?? 40 + Math.floor(Math.random() * 11), // 40–50
  }));

  const [addresses] = useState([
    {
      id: 1,
      name: "John Doe",
      phone: "9876543210",
      house: "123, ABC Apartments, MG Road",
      city: "Bangalore",
      pincode: "560001",
      state: "Karnataka",
      default: true,
    },
  ]);

  const [selectedAddressId, setSelectedAddressId] = useState(1);

  // PRICE CALCULATIONS
  const subtotal = itemsWithDelivery.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const deliveryCharges = itemsWithDelivery.reduce(
    (sum, item) => sum + item.deliveryCharge * item.quantity,
    0
  );

  const totalAmount = subtotal + deliveryCharges;

  const handleProceed = () => {
    navigate("/payment", {
      state: { itemsWithDelivery, subtotal, deliveryCharges, totalAmount },
    });
  };

  return (
    <div className="checkout-page">
      <h2>Checkout</h2>

      {/* PRODUCTS TABLE */}
      <div className="checkout-table">
        <div className="checkout-header">
          <span>Product</span>
          <span>Price</span>
          <span>Qty</span>
          <span>Delivery</span>
        </div>

        {itemsWithDelivery.map((item) => (
          <div className="checkout-row" key={item.id}>
            <div className="product-cell">
              <img src={item.image} alt={item.name} />
              <span>{item.name}</span>
            </div>

            <span>₹{item.price}</span>
            <span>{item.quantity}</span>
            <span className="delivery-fee">
              ₹{item.deliveryCharge}
            </span>
          </div>
        ))}
      </div>

      {/* DELIVERY ADDRESS */}
      <div className="checkout-address">
        <h3>Delivery Address</h3>

        {addresses.map((addr) => (
          <label key={addr.id} className="address-card">
            <input
              type="radio"
              checked={selectedAddressId === addr.id}
              onChange={() => setSelectedAddressId(addr.id)}
            />
            <div>
              <strong>{addr.name}</strong> | {addr.phone}
              <p>
                {addr.house}, {addr.city} - {addr.pincode}
              </p>
              <p>{addr.state}</p>
            </div>
          </label>
        ))}
      </div>

      {/* PRICE BREAKDOWN */}
      <div className="price-box">
        <div className="price-row">
          <span>Subtotal</span>
          <span>₹{subtotal}</span>
        </div>

        <div className="price-row">
          <span>Delivery Charges</span>
          <span>₹{deliveryCharges}</span>
        </div>

        <hr />

        <div className="price-row total">
          <strong>Total</strong>
          <strong>₹{totalAmount}</strong>
        </div>
      </div>

      {/* SINGLE PROCEED BUTTON */}
      <button className="proceed-btn" onClick={handleProceed}>
        Proceed to Payment
      </button>
    </div>
  );
};

export default Checkout;
