// src/DeliveryAddress.js
import React, { useEffect, useState } from "react";
import "./DeliveryAddress.css";

/**
 * DeliveryAddress
 * Props:
 *  - onUse(address) optional callback fired when user clicks "Use This Address"
 *
 * This component stores addresses in localStorage so they persist across pages.
 * It provides:
 *  - + Add New Address (opens the form)
 *  - Save address (adds to list)
 *  - Mark address as default
 *  - "Use This Address" button for each address (calls onUse or logs)
 */
const STORAGE_KEY = "mystore_addresses_v1";

const DeliveryAddress = ({ onUse } = {}) => {
  const [addresses, setAddresses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [form, setForm] = useState({
    country: "",
    fullname: "",
    mobile: "",
    house: "",
    area: "",
    landmark: "",
    pincode: "",
    city: "",
    state: "",
    defaultAddress: false,
  });

  // load saved addresses from localStorage
  useEffect(() => {
    try {
      const raw = localStorage.getItem(STORAGE_KEY);
      if (raw) setAddresses(JSON.parse(raw));
    } catch (e) {
      console.warn("Failed reading addresses from storage", e);
    }
  }, []);

  // persist addresses
  const persist = (list) => {
    setAddresses(list);
    try {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(list));
    } catch (e) {
      console.warn("Failed saving addresses", e);
    }
  };

  // handle input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({ ...prev, [name]: type === "checkbox" ? checked : value }));
  };

  // submit new address
  const handleSave = (e) => {
    e.preventDefault();

    // simple validation
    if (!form.fullname.trim() || !form.mobile.trim() || !form.pincode.trim()) {
      alert("Please fill required fields: Full name, Mobile, Pincode");
      return;
    }

    // if defaultAddress true, unset previous defaults
    const next = form.defaultAddress
      ? addresses.map((a) => ({ ...a, defaultAddress: false }))
      : addresses.slice();

    const newAddr = { ...form, id: Date.now() };
    const merged = [...next, newAddr];
    persist(merged);
    setForm({
      country: "",
      fullname: "",
      mobile: "",
      house: "",
      area: "",
      landmark: "",
      pincode: "",
      city: "",
      state: "",
      defaultAddress: false,
    });
    setShowForm(false);
  };

  // toggle default flag for an existing saved address
  const markDefault = (id) => {
    const next = addresses.map((a) => (a.id === id ? { ...a, defaultAddress: true } : { ...a, defaultAddress: false }));
    persist(next);
  };

  // delete an address
  const deleteAddress = (id) => {
    if (!window.confirm("Delete this address?")) return;
    persist(addresses.filter((a) => a.id !== id));
  };

  // Use an address (fires prop callback if provided, otherwise logs)
  const useAddress = (address) => {
    if (typeof onUse === "function") {
      onUse(address);
    } else {
      console.log("Address selected for use:", address);
      // default behaviour: show a small toast-like alert
      alert("Address selected — continue to payment/next step.");
    }
  };

  return (
    <div className="delivery-address-root">
      <div className="address-header">
        <h3>Delivery Address</h3>
        <button className="add-address-btn" onClick={() => setShowForm((s) => !s)}>
          + Add New Address
        </button>
      </div>

      {showForm && (
        <form className="address-form" onSubmit={handleSave}>
          <div className="two-col">
            <input name="country" value={form.country} onChange={handleChange} placeholder="Country" />
            <input name="fullname" value={form.fullname} onChange={handleChange} placeholder="Full Name *" required />
          </div>

          <div className="two-col">
            <input name="mobile" value={form.mobile} onChange={handleChange} placeholder="Mobile Number *" required />
            <input name="pincode" value={form.pincode} onChange={handleChange} placeholder="Pincode *" required />
          </div>

          <input name="house" value={form.house} onChange={handleChange} placeholder="Flat / House No / Building" />
          <input name="area" value={form.area} onChange={handleChange} placeholder="Area / Street / Locality" />
          <input name="landmark" value={form.landmark} onChange={handleChange} placeholder="Landmark (optional)" />
          <div className="two-col">
            <input name="city" value={form.city} onChange={handleChange} placeholder="Town / City" />
            <input name="state" value={form.state} onChange={handleChange} placeholder="State" />
          </div>

          <label className="default-row">
            <input type="checkbox" name="defaultAddress" checked={form.defaultAddress} onChange={handleChange} />
            Make this my default address
          </label>

          <div className="form-actions">
            <button type="submit" className="use-address-btn">Use This Address</button>
            <button type="button" className="cancel-btn" onClick={() => setShowForm(false)}>Cancel</button>
          </div>
        </form>
      )}

      {/* Saved addresses list */}
      <div className="saved-list">
        {addresses.length === 0 ? (
          <p className="muted">No saved addresses yet — add one using “+ Add New Address”.</p>
        ) : (
          addresses.slice().reverse().map((addr) => (
            <div className={`addr-card ${addr.defaultAddress ? "addr-default" : ""}`} key={addr.id}>
              <div className="addr-main">
                <div className="addr-title">
                  <strong>{addr.fullname}</strong>
                  {addr.defaultAddress && <span className="badge">Default</span>}
                </div>
                <div className="addr-line">{addr.house}{addr.house && addr.area ? ", " : ""}{addr.area}</div>
                <div className="addr-line">{addr.city} - {addr.pincode}</div>
                <div className="addr-line">{addr.state}{addr.country ? `, ${addr.country}` : ""}</div>
                <div className="addr-line muted">Mobile: {addr.mobile}{addr.landmark ? ` • Landmark: ${addr.landmark}` : ""}</div>
              </div>

              <div className="addr-actions">
                <button className="use-btn" onClick={() => useAddress(addr)}>Use</button>
                <button className="small-btn" onClick={() => markDefault(addr.id)}>Set Default</button>
                <button className="small-danger" onClick={() => deleteAddress(addr.id)}>Delete</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default DeliveryAddress;
