// src/Cart.js
import React, { useContext, useState } from "react";
import { StoreContext } from "./StoreContext";
import "./Cart.css";
import { useNavigate } from "react-router-dom";


const Cart = () => {
  const { cart, removeFromCart } = useContext(StoreContext);
  const navigate = useNavigate();


  // Track selected items
  const [selected, setSelected] = useState([]);

  const toggleSelect = (id) => {
    if (selected.includes(id)) {
      setSelected(selected.filter((x) => x !== id));
    } else {
      setSelected([...selected, id]);
    }
  };

  const [quantities, setQuantities] = useState(
    cart.reduce((acc, item) => {
      acc[item.id] = 1;
      return acc;
    }, {})
  );

  const increaseQty = (id) => {
    setQuantities((prev) => ({
      ...prev,
      [id]: prev[id] + 1,
    }));
  };

  const decreaseQty = (id) => {
    setQuantities((prev) => ({
      ...prev,
      [id]: prev[id] > 1 ? prev[id] - 1 : 1,
    }));
  };

  // Selected items total
  const selectedTotal = cart
    .filter((item) => selected.includes(item.id))
    .reduce((sum, item) => sum + item.price * quantities[item.id], 0);

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>

      {cart.length === 0 ? <p>Your cart is empty.</p> : null}

      {cart.map((item) => (
        <div className="cart-item" key={item.id}>
          {/* Checkbox */}
          <input
            type="checkbox"
            checked={selected.includes(item.id)}
            onChange={() => toggleSelect(item.id)}
            className="checkbox"
          />

          {/* Product Image */}
          <img src={item.image} alt={item.name} className="cart-img" />

          {/* Product Details */}
          <div className="cart-info">
            <h3>{item.name}</h3>
            <p>Price: ₹{item.price}</p>

            {/* Quantity Controls */}
            <div className="qty-controls">
              <button onClick={() => decreaseQty(item.id)}>-</button>
              <span>{quantities[item.id]}</span>
              <button onClick={() => increaseQty(item.id)}>+</button>
            </div>

            {/* Remove Button */}
            <button
              className="delete-btn"
              onClick={() => removeFromCart(item.id)}
            >
              Delete
            </button>
          </div>

          {/* Total for this item */}
          <p className="item-total">
            ₹{item.price * quantities[item.id]}
          </p>
        </div>
      ))}

      {/* Proceed To Buy Button */}
<div className="proceed-box">
  <button
    className="proceed-btn"
    onClick={() => navigate("/address")}
  >
    Proceed to Buy ({selectedItems.length} items)
  </button>
</div>


      {/* Selected Total */}
      <div className="cart-totals">
        <div className="totals-row">
          <span>Selected Total:</span>
          <strong>₹{selectedTotal}</strong>
        </div>
      </div>
    </div>
  );
};

export default Cart;
