// src/Checkout.js
const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Normalize selectedItems to always be an array
  const selectedItems = Array.isArray(location.state)
    ? location.state
    : location.state
    ? [location.state] // single product converted to array
    : [];

  const [selectedAddress, setSelectedAddress] = React.useState(null);

  return (
    <div className="Checkout-page">
      <h2>Checkout</h2>

      {/* Show Selected Products */}
      <div className="checkout-products">
        {selectedItems.map((product) => (
          <div className="Checkout-product" key={product.id}>
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>Price: â‚¹{product.price * product.quantity}</p>
            <p>Quantity: {product.quantity}</p>
          </div>
        ))}
      </div>

      {/* Delivery Address */}
      <h3>Delivery Address</h3>
      <DeliveryAddress
        selectedAddress={selectedAddress}
        setSelectedAddress={setSelectedAddress}
      />

      {/* Proceed Button */}
      <button
        className="place-order-btn"
        disabled={!selectedAddress}
        onClick={() =>
          navigate("/payment", {
            state: { selectedItems, selectedAddress },
          })
        }
      >
        Proceed to Payment
      </button>
    </div>
  );
};
