// src/Checkout.js
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Checkout.css";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Get selected items passed from cart or Buy Now
  const selectedItems = location.state?.selectedItems || [];

  // Example addresses (in real app, this can come from user data)
  const [addresses, setAddresses] = useState([
    {
      id: 1,
      name: "John Doe",
      phone: "9876543210",
      house: "123, ABC Apartments, MG Road",
      city: "Bangalore",
      pincode: "560001",
      state: "Karnataka",
      default: true,
    },
    {
      id: 2,
      name: "Jane Smith",
      phone: "9876543211",
      house: "456, XYZ Street, 2nd Block",
      city: "Bangalore",
      pincode: "560002",
      state: "Karnataka",
      default: false,
    },
  ]);

  const [selectedAddressId, setSelectedAddressId] = useState(
    addresses.find((addr) => addr.default)?.id || null
  );

  const handleProceed = () => {
    if (!selectedAddressId) {
      alert("Please select a delivery address.");
      return;
    }
    navigate("/payment", { state: { selectedItems, selectedAddressId } });
  };

  // ✅ PRICE CALCULATIONS
const subtotal = selectedItems.reduce(
  (sum, item) => sum + item.price * item.quantity,
  0
);

const deliveryFee = subtotal > 0 ? 40 : 0; // flat delivery fee
const totalAmount = subtotal + deliveryFee;

  return (
    <div className="checkout-page">
      <h2>Checkout</h2>

      {/* Products Section */}
      <div className="checkout-products">
        {selectedItems.length === 0 ? (
          <p>No products selected.</p>
        ) : (
          selectedItems.map((item) => (
            <div className="checkout-product" key={item.id}>
              <img src={item.image} alt={item.name} />
              <div className="product-info">
                <h3>{item.name}</h3>
                <p>Price: ₹{item.price}</p>
                <p>Quantity: {item.quantity}</p>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Delivery Address Section */}
      <div className="checkout-address">
        <h3>Delivery Address</h3>
        {addresses.map((addr) => (
          <label key={addr.id} className="address-card">
            <input
              type="radio"
              name="selectedAddress"
              value={addr.id}
              checked={selectedAddressId === addr.id}
              onChange={() => setSelectedAddressId(addr.id)}
            />
            <div className="address-info">
              <p>
                <strong>{addr.name}</strong> | {addr.phone}
              </p>
              <p>
                {addr.house}, {addr.city}, {addr.pincode}, {addr.state}
              </p>
              {addr.default && <span className="default-badge">Default</span>}
            </div>
          </label>
        ))}
      </div>

      {/* Proceed Button */}
      <div className="checkout-proceed">
        <button className="proceed-btn" onClick={handleProceed}>
          Proceed to Payment
        </button>
      </div>
    </div>
  );
};

export default Checkout;
