// src/AddressSection.js
import React, { useState } from "react";
import "./AddressSection.css";

const AddressSection = () => {
  const [showForm, setShowForm] = useState(true);
  const [addresses, setAddresses] = useState([]);

  const [formData, setFormData] = useState({
    fullname: "",
    mobile: "",
    country: "",
    house: "",
    area: "",
    city: "",
    state: "",
    pincode: "",
    defaultAddress: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({ ...prev, [name]: type === "checkbox" ? checked : value }));
  };

  const saveAddress = (e) => {
    e.preventDefault();
    setAddresses([...addresses, formData]);
    alert("Address Saved!");
    setShowForm(false);
  };

  return (
    <div className="address-section">

      {/* + ADD NEW ADDRESS BUTTON */}
      <button className="add-address-btn" onClick={() => setShowForm(true)}>
        + Add New Address
      </button>

      {/* ADDRESS FORM */}
      {showForm && (
        <form className="address-form" onSubmit={saveAddress}>
          <input type="text" name="fullname" placeholder="Full Name" required onChange={handleChange} />
          <input type="text" name="mobile" placeholder="Mobile Number" required onChange={handleChange} />
          <input type="text" name="country" placeholder="Country" required onChange={handleChange} />
          <input type="text" name="house" placeholder="House No / Street" required onChange={handleChange} />
          <input type="text" name="area" placeholder="Area / Locality / Landmark" required onChange={handleChange} />
          <input type="text" name="city" placeholder="Town / City" required onChange={handleChange} />
          <input type="text" name="state" placeholder="State" required onChange={handleChange} />
          <input type="text" name="pincode" placeholder="Pincode" required onChange={handleChange} />

          <label className="default-checkbox">
            <input type="checkbox" name="defaultAddress" onChange={handleChange} />
            Make this my default address
          </label>

          <button type="submit" className="use-address-btn">
            Use This Address
          </button>
        </form>
      )}
    </div>
  );
};

export default AddressSection;
