// src/Checkout.js
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Checkout.css";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // ðŸ”¥ Works for Cart + Buy Now
  const rawItems =
    location.state?.selectedItems || location.state?.product || [];
  const selectedItems = Array.isArray(rawItems) ? rawItems : [rawItems];

  // Example addresses
  const [addresses] = useState([
    {
      id: 1,
      name: "John Doe",
      phone: "9876543210",
      house: "123, ABC Apartments, MG Road",
      city: "Bangalore",
      pincode: "560001",
      state: "Karnataka",
      default: true,
    },
    {
      id: 2,
      name: "Jane Smith",
      phone: "9876543211",
      house: "456, XYZ Street, 2nd Block",
      city: "Bangalore",
      pincode: "560002",
      state: "Karnataka",
      default: false,
    },
  ]);

  const [selectedAddressId, setSelectedAddressId] = useState(
    addresses.find((a) => a.default)?.id || null
  );

  // ðŸ”¹ PRICE CALCULATION (UNCHANGED)
  const subtotal = selectedItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  // ðŸ”¥ NEW: delivery charge per product (â‚¹40â€“â‚¹50)
  const deliveryCharges = selectedItems.reduce((sum, item) => {
    const perItemDelivery = item.deliveryCharge || (40 + Math.floor(Math.random() * 11));
    item.deliveryCharge = perItemDelivery; // attach once
    return sum + perItemDelivery * item.quantity;
  }, 0);

  const totalAmount = subtotal + deliveryCharges;

  const handleProceed = () => {
    if (!selectedAddressId) {
      alert("Please select a delivery address");
      return;
    }

    navigate("/payment", {
      state: {
        selectedItems,
        selectedAddressId,
        subtotal,
        deliveryCharges,
        totalAmount,
      },
    });
  };

  return (
    <div className="checkout-page">
      <h2>Checkout</h2>

      {/* PRODUCTS */}
      <div className="checkout-products">
        {selectedItems.length === 0 ? (
          <p>No products selected.</p>
        ) : (
          selectedItems.map((item) => (
            <div className="checkout-product" key={item.id}>
              <img
                src={item.image}
                alt={item.name}
                className="checkout-img"
              />

              <div className="product-info">
                <h3>{item.name}</h3>
                <p>Price: â‚¹{item.price}</p>
                <p>Qty: {item.quantity}</p>
                <p className="delivery-fee">
                  Delivery: â‚¹{item.deliveryCharge} / item
                </p>
              </div>
            </div>
          ))
        )}
      </div>

      {/* ADDRESS */}
      <div className="checkout-address">
        <h3>Delivery Address</h3>

        {addresses.map((addr) => (
          <label key={addr.id} className="address-card">
            <input
              type="radio"
              checked={selectedAddressId === addr.id}
              onChange={() => setSelectedAddressId(addr.id)}
            />

            <div className="address-info">
              <strong>{addr.name}</strong> | {addr.phone}
              <p>
                {addr.house}, {addr.city} - {addr.pincode}
              </p>
              <p>{addr.state}</p>
              {addr.default && <span className="default-badge">Default</span>}
            </div>
          </label>
        ))}
      </div>

      {/* PRICE BREAKDOWN */}
      <div className="price-box">
        <div className="price-row">
          <span>Subtotal</span>
          <span>â‚¹{subtotal}</span>
        </div>

        <div className="price-row">
          <span>Delivery Charges</span>
          <span>â‚¹{deliveryCharges}</span>
        </div>

        <hr />

        <div className="price-row total">
          <strong>Total</strong>
          <strong>â‚¹{totalAmount}</strong>
        </div>
      </div>

      {/* SINGLE PROCEED BUTTON */}
      <div className="checkout-proceed">
        <button className="proceed-btn" onClick={handleProceed}>
          Proceed to Payment
        </button>
      </div>
    </div>
  );
};

export default Checkout;
