// src/ProductDetails.js
import React, { useContext, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { StoreContext } from "./StoreContext";
import { products } from "./data";

const ProductDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useContext(StoreContext);

  const product = products.find((p) => p.id === Number(id));

  const [qty, setQty] = useState(1);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");

  if (!product) {
    return <h2 style={{ padding: "20px" }}>Product not found</h2>;
  }

  const productWithQty = { ...product, quantity: qty, selected: true };

  const addReview = () => {
    if (!comment) return;
    setReviews([...reviews, { rating, comment }]);
    setComment("");
  };

  return (
    <div className="product-details-page">
      <img src={product.image} alt={product.name} className="product-details-image" />

      <h2>{product.name}</h2>
      <p className="price">₹{product.price}</p>

      {/* QUANTITY */}
      <div className="qty-box">
        <button onClick={() => setQty(Math.max(1, qty - 1))}>-</button>
        <span>{qty}</span>
        <button onClick={() => setQty(qty + 1)}>+</button>
      </div>

      <button className="add-btn" onClick={() => addToCart(productWithQty)}>
        Add to Cart
      </button>

      <button
        className="buy-btn"
        onClick={() => navigate("/checkout", { state: [productWithQty] })}
      >
        Buy Now
      </button>

      {/* REVIEWS */}
      <div className="reviews">
        <h3>Reviews</h3>

        {reviews.length === 0 && <p>No reviews yet.</p>}

        {reviews.map((r, i) => (
          <p key={i}>⭐ {r.rating}/5 – {r.comment}</p>
        ))}

        <select value={rating} onChange={(e) => setRating(e.target.value)}>
          {[5, 4, 3, 2, 1].map((r) => (
            <option key={r} value={r}>{r}</option>
          ))}
        </select>

        <input
          placeholder="Write a review"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />

        <button onClick={addReview}>Submit Review</button>
      </div>
    </div>
  );
};

export default ProductDetails;
