// src/Checkout.js
import React, { useState, useMemo } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Checkout.css";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // ðŸ”¥ SAFELY COLLECT PRODUCTS FROM ANY FLOW
  const incoming =
    location.state?.selectedItems ||
    location.state?.items ||
    location.state?.product ||
    [];

  // ðŸ”¥ ALWAYS MAKE IT AN ARRAY
  const products = Array.isArray(incoming) ? incoming : [incoming];

  // ðŸ”¥ ADD DELIVERY CHARGE PER PRODUCT (â‚¹40â€“â‚¹50)
  const productsWithDelivery = useMemo(() => {
    return products.map((item) => ({
      ...item,
      quantity: item.quantity || 1,
      deliveryCharge:
        item.deliveryCharge ?? 40 + Math.floor(Math.random() * 11),
    }));
  }, [products]);

  // ---------------- ADDRESSES ----------------
  const [addresses] = useState([
    {
      id: 1,
      name: "John Doe",
      phone: "9876543210",
      address: "123, ABC Apartments, MG Road",
      city: "Bangalore",
      pincode: "560001",
      state: "Karnataka",
      default: true,
    },
    {
      id: 2,
      name: "Jane Smith",
      phone: "9876543211",
      address: "456, XYZ Street, 2nd Block",
      city: "Bangalore",
      pincode: "560002",
      state: "Karnataka",
      default: false,
    },
  ]);

  const [selectedAddressId, setSelectedAddressId] = useState(
    addresses.find((a) => a.default)?.id
  );

  // ---------------- PRICE CALCULATIONS ----------------
  const subtotal = productsWithDelivery.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const deliveryTotal = productsWithDelivery.reduce(
    (sum, item) => sum + item.deliveryCharge * item.quantity,
    0
  );

  const totalAmount = subtotal + deliveryTotal;

  const handleProceed = () => {
    if (!selectedAddressId) {
      alert("Please select a delivery address");
      return;
    }

    navigate("/payment", {
      state: {
        products: productsWithDelivery,
        subtotal,
        deliveryTotal,
        totalAmount,
      },
    });
  };

  return (
    <div className="checkout-page">
      <h2>Checkout</h2>

      {/* âœ… PRODUCTS ALWAYS SHOWN */}
      <div className="checkout-products">
        {productsWithDelivery.map((item) => (
          <div className="checkout-product" key={item.id}>
            <img src={item.image} alt={item.name} />
            <div className="product-info">
              <h4>{item.name}</h4>
              <p>â‚¹{item.price}</p>
              <p>Qty: {item.quantity}</p>
              <p className="delivery-fee">
                Delivery: â‚¹{item.deliveryCharge}
              </p>
            </div>
          </div>
        ))}
      </div>

      {/* DELIVERY ADDRESS */}
      <div className="checkout-address">
        <h3>Delivery Address</h3>

        {addresses.map((addr) => (
          <label key={addr.id} className="address-card">
            <input
              type="radio"
              checked={selectedAddressId === addr.id}
              onChange={() => setSelectedAddressId(addr.id)}
            />
            <div>
              <strong>{addr.name}</strong> | {addr.phone}
              <p>
                {addr.address}, {addr.city} - {addr.pincode}
              </p>
              <p>{addr.state}</p>
              {addr.default && (
                <span className="default-badge">Default</span>
              )}
            </div>
          </label>
        ))}
      </div>

      {/* PRICE BREAKDOWN */}
      <div className="price-box">
        <div className="price-row">
          <span>Subtotal</span>
          <span>â‚¹{subtotal}</span>
        </div>

        <div className="price-row">
          <span>Delivery Charges</span>
          <span>â‚¹{deliveryTotal}</span>
        </div>

        <hr />

        <div className="price-row total">
          <strong>Total</strong>
          <strong>â‚¹{totalAmount}</strong>
        </div>
      </div>

      {/* SINGLE BUTTON */}
      <button className="proceed-btn" onClick={handleProceed}>
        Proceed to Payment
      </button>
    </div>
  );
};

export default Checkout;
