// src/DeliveryAddress.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DeliveryAddress.css";

const DeliveryAddress = () => {
  const navigate = useNavigate();

  // Initial addresses array
  const [addresses, setAddresses] = useState([
    {
      id: 1,
      fullName: "John Doe",
      mobile: "9876543210",
      flat: "123, ABC Apartments",
      area: "MG Road",
      landmark: "Near Park",
      pincode: "560001",
      city: "Bangalore",
      state: "Karnataka",
      isDefault: true,
    },
  ]);

  const [selectedAddress, setSelectedAddress] = useState(null);
  const [showAddNew, setShowAddNew] = useState(false);

  const [newAddress, setNewAddress] = useState({
    fullName: "",
    mobile: "",
    flat: "",
    area: "",
    landmark: "",
    pincode: "",
    city: "",
    state: "",
    isDefault: false,
  });

  // Handle selection of an address
  const onSelectAddress = (address) => {
    setSelectedAddress(address);
  };

  // Handle input change for new address
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setNewAddress((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  // Add new address to the list
  const handleAddAddress = (e) => {
    e.preventDefault();
    const id = addresses.length + 1;
    setAddresses((prev) => [...prev, { id, ...newAddress }]);
    setNewAddress({
      fullName: "",
      mobile: "",
      flat: "",
      area: "",
      landmark: "",
      pincode: "",
      city: "",
      state: "",
      isDefault: false,
    });
    setShowAddNew(false);
  };

  return (
    <div className="delivery-address-page">
      <h2>Delivery Address</h2>

      {/* Existing addresses */}
      <div className="addresses-list">
        {addresses.map((address) => (
          <div key={address.id} className="address-card">
            <input
              type="radio"
              name="selectedAddress"
              checked={selectedAddress?.id === address.id}
              onChange={() => onSelectAddress(address)}
            />
            <div className="address-details">
              <p><strong>{address.fullName}</strong> | {address.mobile}</p>
              <p>{address.flat}, {address.area}, {address.landmark}</p>
              <p>{address.city} - {address.pincode}, {address.state}</p>
              {address.isDefault && <p className="default-label">Default</p>}
            </div>
          </div>
        ))}
      </div>

      {/* Add new address */}
      <button
        className="add-new-btn"
        onClick={() => setShowAddNew(!showAddNew)}
      >
        + Add New Address
      </button>

      {showAddNew && (
        <form className="add-address-form" onSubmit={handleAddAddress}>
          <input
            type="text"
            name="fullName"
            placeholder="Full Name"
            value={newAddress.fullName}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="mobile"
            placeholder="Mobile Number"
            value={newAddress.mobile}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="flat"
            placeholder="Flat/House No"
            value={newAddress.flat}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="area"
            placeholder="Area/Street"
            value={newAddress.area}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="landmark"
            placeholder="Landmark"
            value={newAddress.landmark}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="pincode"
            placeholder="Pincode"
            value={newAddress.pincode}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="city"
            placeholder="Town/City"
            value={newAddress.city}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="state"
            placeholder="State"
            value={newAddress.state}
            onChange={handleInputChange}
            required
          />
          <label>
            <input
              type="checkbox"
              name="isDefault"
              checked={newAddress.isDefault}
              onChange={handleInputChange}
            /> Make this default address
          </label>
          <button type="submit" className="save-address-btn">Save Address</button>
        </form>
      )}

      {/* Proceed to Payment */}
      <button
        className="proceed-btn"
        disabled={!selectedAddress}
        onClick={() => navigate("/PaymentPage", { state: selectedAddress })}
      >
        Proceed to Payment
      </button>
    </div>
  );
};

export default DeliveryAddress;
