// src/DeliveryAddress.js
import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./DeliveryAddress.css";

const DeliveryAddress = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const selectedItems = location.state?.selectedItems || [];

  // Existing addresses
  const [addresses, setAddresses] = useState([
    {
      id: 1,
      name: "John Doe",
      phone: "9876543210",
      house: "123, ABC Apartments, MG Road",
      city: "Bangalore",
      pincode: "560001",
      state: "Karnataka",
      default: true,
    },
  ]);

  const [selectedAddressId, setSelectedAddressId] = useState(1);

  // Toggle form
  const [showForm, setShowForm] = useState(false);

  // New address form state
  const [newAddress, setNewAddress] = useState({
    name: "",
    phone: "",
    house: "",
    city: "",
    pincode: "",
    state: "",
  });

  // Add address
  const handleAddAddress = () => {
    if (
      !newAddress.name ||
      !newAddress.phone ||
      !newAddress.house ||
      !newAddress.city ||
      !newAddress.pincode ||
      !newAddress.state
    ) {
      alert("Please fill all address fields");
      return;
    }

    const addressToAdd = {
      ...newAddress,
      id: Date.now(),
      default: false,
    };

    setAddresses([...addresses, addressToAdd]);
    setSelectedAddressId(addressToAdd.id);
    setShowForm(false);

    setNewAddress({
      name: "",
      phone: "",
      house: "",
      city: "",
      pincode: "",
      state: "",
    });
  };

  const handleProceed = () => {
    const selectedAddress = addresses.find(
      (addr) => addr.id === selectedAddressId
    );

    if (!selectedAddress) {
      alert("Please select an address");
      return;
    }

    navigate("/payment", {
      state: {
        selectedItems,
        selectedAddress,
      },
    });
  };

  return (
    <div className="delivery-page">
      <h2>Delivery Address</h2>

      {/* ADDRESS LIST */}
      {addresses.map((addr) => (
        <label className="address-card" key={addr.id}>
          <input
            type="radio"
            name="address"
            checked={selectedAddressId === addr.id}
            onChange={() => setSelectedAddressId(addr.id)}
          />

          <div className="address-info">
            <p>
              <strong>{addr.name}</strong> | {addr.phone}
            </p>
            <p>
              {addr.house}, {addr.city} - {addr.pincode}
            </p>
            <p>{addr.state}</p>
            {addr.default && <span className="default-badge">Default</span>}
          </div>
        </label>
      ))}

      {/* ADD NEW ADDRESS BUTTON */}
      <button
        className="add-address-btn"
        onClick={() => setShowForm(!showForm)}
      >
        + Add New Address
      </button>

      {/* ADD ADDRESS FORM */}
      {showForm && (
        <div className="address-form">
          <input
            placeholder="Full Name"
            value={newAddress.name}
            onChange={(e) =>
              setNewAddress({ ...newAddress, name: e.target.value })
            }
          />
          <input
            placeholder="Phone Number"
            value={newAddress.phone}
            onChange={(e) =>
              setNewAddress({ ...newAddress, phone: e.target.value })
            }
          />
          <input
            placeholder="House / Street"
            value={newAddress.house}
            onChange={(e) =>
              setNewAddress({ ...newAddress, house: e.target.value })
            }
          />
          <input
            placeholder="City"
            value={newAddress.city}
            onChange={(e) =>
              setNewAddress({ ...newAddress, city: e.target.value })
            }
          />
          <input
            placeholder="Pincode"
            value={newAddress.pincode}
            onChange={(e) =>
              setNewAddress({ ...newAddress, pincode: e.target.value })
            }
          />
          <input
            placeholder="State"
            value={newAddress.state}
            onChange={(e) =>
              setNewAddress({ ...newAddress, state: e.target.value })
            }
          />

          <button className="save-address-btn" onClick={handleAddAddress}>
            Save Address
          </button>
        </div>
      )}

      {/* PROCEED */}
      <button
        className="proceed-btn"
        disabled={!selectedAddressId}
        onClick={handleProceed}
      >
        Proceed to Payment
      </button>
    </div>
  );
};

export default DeliveryAddress;
