// src/Cart.js
import React, { useContext, useState, useMemo } from "react";
import { StoreContext } from "./StoreContext";
import "./Cart.css";

const Cart = () => {
  const { cart, setCart, removeFromCart } = useContext(StoreContext);

  // local checked state derived from cart (keeps checked flags in cart items)
  // We will store checked status inside each cart item as `checked` (boolean)
  // to preserve selection across re-renders.
  const ensureCheckedFlags = () => {
    // If an item doesn't have checked, default to true (or false as you prefer)
    return cart.map((it) => (typeof it.checked === "boolean" ? it : { ...it, checked: true }));
  };

  // If cart items are missing checked fields, initialize them
  React.useEffect(() => {
    const needsInit = cart.some((it) => typeof it.checked !== "boolean");
    if (needsInit) {
      setCart(ensureCheckedFlags());
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [cart]);

  // increment quantity
  const increaseQty = (id) => {
    setCart((prev) =>
      prev.map((item) =>
        item.id === id ? { ...item, quantity: (item.quantity || 1) + 1 } : item
      )
    );
  };

  // decrease quantity (if becomes 0 -> remove)
  const decreaseQty = (id) => {
    const target = cart.find((it) => it.id === id);
    if (!target) return;

    const currentQty = target.quantity || 1;
    if (currentQty <= 1) {
      // remove item
      removeFromCart(id);
    } else {
      setCart((prev) =>
        prev.map((item) =>
          item.id === id ? { ...item, quantity: currentQty - 1 } : item
        )
      );
    }
  };

  // toggle checkbox stored on item.checked
  const toggleCheck = (id) => {
    setCart((prev) => prev.map((item) => (item.id === id ? { ...item, checked: !item.checked } : item)));
  };

  // remove item by delete button
  const handleDelete = (id) => {
    removeFromCart(id);
  };

  // Selected items total (checked items)
  const selectedTotal = useMemo(() => {
    return cart
      .filter((it) => it.checked)
      .reduce((sum, it) => sum + (it.price || 0) * (it.quantity || 1), 0);
  }, [cart]);

  // Grand total for the cart (all items)
  const cartTotal = useMemo(() => {
    return cart.reduce((sum, it) => sum + (it.price || 0) * (it.quantity || 1), 0);
  }, [cart]);

  return (
    <div className="cart-container">
      <h2 className="cart-title">Your Cart</h2>

      {cart.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <>
          <div className="cart-list">
            {cart.map((item) => (
              <div className="cart-item" key={item.id}>
                <input
                  type="checkbox"
                  className="cart-checkbox"
                  checked={!!item.checked}
                  onChange={() => toggleCheck(item.id)}
                />

                <img src={item.image} alt={item.name} className="cart-img" />

                <div className="cart-info">
                  <h3 className="cart-name">{item.name}</h3>
                  <p className="single-price">₹{item.price}</p>
                </div>

                <div className="qty-controls">
                  <button className="qty-btn" onClick={() => decreaseQty(item.id)}>-</button>
                  <span className="qty-value">{item.quantity || 1}</span>
                  <button className="qty-btn" onClick={() => increaseQty(item.id)}>+</button>
                </div>

                <div className="item-subtotal">
                  <p>Subtotal</p>
                  <strong>₹{(item.price || 0) * (item.quantity || 1)}</strong>
                </div>

                <button className="delete-btn" onClick={() => handleDelete(item.id)}>
                  Delete
                </button>
              </div>
            ))}
          </div>

          {/* Totals area */}
          <div className="cart-totals">
            <div className="totals-row">
              <span>Selected total:</span>
              <strong>₹{selectedTotal}</strong>
            </div>

            <div className="totals-row">
              <span>Cart total:</span>
              <strong>₹{cartTotal}</strong>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
