import React, { useState } from "react";
import { Link } from "react-router-dom";
import SearchBar from "./SearchBar";
import { categories, products } from "./data";

const Home = () => {
  const [search, setSearch] = useState("");

  // ðŸ”¥ Detect when user presses Enter
  const handleSearchEnter = (value) => {
    console.log("Search triggered:", value);
    // You can add advanced search or navigation here later
  };

  const filtered = products.filter((p) =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="page">
      <h1>E-Commerce Store</h1>

      {/* Pass onEnter to SearchBar */}
      <SearchBar 
        search={search} 
        setSearch={setSearch} 
        onEnter={handleSearchEnter}
      />

      <h2>Categories</h2>
      <div className="grid">
        {categories.map((cat) => (
          <Link key={cat} to={`/category/${cat}`} className="card">
            {cat}
          </Link>
        ))}
      </div>

      <h2>Products</h2>
      <div className="grid">
        {filtered.map((item) => (
          <Link to={`/product/${item.id}`} key={item.id} className="card">
            <img src={item.image} alt="" />
            <h3>{item.name}</h3>
            <p>â‚¹{item.price}</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Home;
