// src/Checkout.js
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Checkout.css";
import DeliveryAddress from "./DeliveryAddress";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Items coming from cart or direct buy
  const selectedItems = location.state || [];

  // Track selected address
  const [selectedAddress, setSelectedAddress] = useState(null);

  return (
    <div className="Checkout-page">
      <h2>Checkout</h2>

      {/* Show Selected Products */}
      <div className="checkout-products">
        {selectedItems.map((product) => (
          <div className="Checkout-product" key={product.id}>
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>Price: â‚¹{product.price * product.quantity}</p>
            <p>Quantity: {product.quantity}</p>
          </div>
        ))}
      </div>

      {/* Delivery Address */}
      <h3>Delivery Address</h3>
      <DeliveryAddress
        selectedAddress={selectedAddress}
        setSelectedAddress={setSelectedAddress}
      />

      {/* Proceed Button */}
      <button
        className="place-order-btn"
        disabled={!selectedAddress} // Disable until address selected
        onClick={() => navigate("/payment", { state: { selectedItems, selectedAddress } })}
      >
        Proceed to Payment
      </button>
    </div>
  );
};

export default Checkout;
