// src/Cart.js
import React, { useContext } from "react";
import { StoreContext } from "./StoreContext";
import { useNavigate } from "react-router-dom";
import "./Cart.css";

const Cart = () => {
  const { cart, updateQuantity, removeFromCart, toggleSelect } =
    useContext(StoreContext);

  const navigate = useNavigate();

  // ✅ Selected items (declared before JSX)
  const selectedItems = cart.filter((item) => item.selected);

  // ✅ Selected total price
  const selectedTotal = selectedItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>

      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {cart.map((item) => (
            <div className="cart-item" key={item.id}>
              {/* Checkbox */}
              <input
                type="checkbox"
                checked={item.selected || false}
                onChange={() => toggleSelect(item.id)}
                className="select-box"
              />

              {/* Product Image */}
              <img src={item.image} alt={item.name} />

              {/* Details */}
              <div className="item-info">
                <h3>{item.name}</h3>
                <p>₹{item.price * item.quantity}</p>

                {/* Quantity Controller */}
                <div className="qty-box">
                  <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>
                    -
                  </button>

                  <span>{item.quantity}</span>

                  <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>
                    +
                  </button>
                </div>
              </div>

              {/* Delete Button */}
              <button
                className="delete-btn"
                onClick={() => removeFromCart(item.id)}
              >
                Delete
              </button>
            </div>
          ))}

          {/* Selected Total Section */}
          <div className="selected-summary">
            <h3>
              Selected Total: <span>₹{selectedTotal}</span>
            </h3>
          </div>

          {/* Proceed Button */}
          <div className="proceed-box">
            <button
              className="proceed-btn"
              onClick={() => navigate("/address")}
              disabled={selectedItems.length === 0} // disable if nothing selected
            >
              Proceed to Buy ({selectedItems.length} items)
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
