import React, { useState } from "react";
import "./DeliveryAddress.css";
import { useNavigate } from "react-router-dom";

const DeliveryAddress = ({ selectedAddress, onSelectAddress, addresses, onAddAddress }) => {
  const navigate = useNavigate();

  const [showForm, setShowForm] = useState(false);
  const [newAddress, setNewAddress] = useState({
    name: "",
    mobile: "",
    house: "",
    street: "",
    city: "",
    pincode: ""
  });

  // Handle form input
  const handleChange = (e) => {
    setNewAddress({ ...newAddress, [e.target.name]: e.target.value });
  };

  // Add new address
  const handleAddAddress = () => {
    if (!newAddress.name || !newAddress.mobile || !newAddress.house || !newAddress.street || !newAddress.city || !newAddress.pincode) {
      alert("Please fill all fields.");
      return;
    }

    onAddAddress({ ...newAddress, id: Date.now() });
    setShowForm(false);

    // Clear form
    setNewAddress({
      name: "",
      mobile: "",
      house: "",
      street: "",
      city: "",
      pincode: ""
    });
  };

  return (
    <div className="delivery-container">
      <h2>Delivery Address</h2>

      {/* Address List */}
      <div className="address-list">
        {addresses.map((addr) => (
          <label key={addr.id} className="address-card">
            <input
              type="checkbox"
              checked={selectedAddress === addr.id}
              onChange={() => onSelectAddress(addr.id)}
            />
            <div className="address-info">
              <p><strong>{addr.name}</strong> ({addr.mobile})</p>
              <p>{addr.house}, {addr.street}</p>
              <p>{addr.city} - {addr.pincode}</p>
            </div>
          </label>
        ))}
      </div>

      {/* + Add New Address Button */}
      <button className="add-address-btn" onClick={() => setShowForm(!showForm)}>
        + Add New Address
      </button>

      {/* New Address Form */}
      {showForm && (
        <div className="address-form">
          <input type="text" name="name" placeholder="Full Name" value={newAddress.name} onChange={handleChange} />
          <input type="text" name="mobile" placeholder="Mobile Number" value={newAddress.mobile} onChange={handleChange} />
          <input type="text" name="house" placeholder="House / Flat No." value={newAddress.house} onChange={handleChange} />
          <input type="text" name="street" placeholder="Street / Area" value={newAddress.street} onChange={handleChange} />
          <input type="text" name="city" placeholder="City" value={newAddress.city} onChange={handleChange} />
          <input type="text" name="pincode" placeholder="Pincode" value={newAddress.pincode} onChange={handleChange} />

          <button className="save-btn" onClick={handleAddAddress}>Save Address</button>
        </div>
      )}

      {/* âœ… Proceed to Payment Button */}
      <button
        className="proceed-btn"
        disabled={!selectedAddress}
        onClick={() => navigate("/payment")}
      >
        Proceed to Payment
      </button>
    </div>
  );
};

export default DeliveryAddress;
